public class Task_3 {    public static void main(String[] args) {        double left = 0;        double right = 5;        double step = 0.5;        double pos = left;        System.out.println("ARG     VALUE");        while (pos <= right) {            System.out.format("%f    %f%n", pos, function(pos));            pos = pos + step;        }    }    private static double function(double arg) {        return Math.tan(2 * arg) - 3;    }}