import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.util.Scanner;public class Main {    static BufferedReader reader;    public static void main(String[] args) {        reader = new BufferedReader(new InputStreamReader(System.in));        Notebook myNotebook = new Notebook("My Notebook");        try {            while (true) {                int i = 1;                System.out.format("%nWhat would you like to do?");                System.out.format("%n%d  Show the list of notes", i++);                System.out.format("%n%d  Add a note", i++);                System.out.format("%n%d  Delete a note", i++);                System.out.format("%n%d  Exit%n", i);                int n = Integer.parseInt(reader.readLine());                switch (n) {                    case 1: {                        NotebookService.printAllNotes(myNotebook.notes, reader);                    }                    case 2: {                        System.out.println("Give your note a title: ");                        String title = reader.readLine();                        System.out.println("Type your text: ");                        String text = reader.readLine();                        NotebookService.addNote(myNotebook.notes, title, text, reader);                    }                    case 3: {                        System.out.println("Choose the note number to delete");                        NotebookService.printAllNotes(myNotebook.notes, reader);                        int j = Integer.parseInt(reader.readLine());                        NotebookService.deleteNote(myNotebook.notes, j);                        System.out.println("The note is deleted");                    }                    case 4: {                        System.out.println("Goodbye!");                        reader.close();                        return;                    }                }            }        } catch (IOException e) {            System.out.println(e.getMessage());        }    }}