import java.text.SimpleDateFormat;import java.util.ArrayList;import java.util.Calendar;import java.util.List;public class Note {    String title;    String textMsg;    String creationDate;    List<Contact> noteContacts;    Note(String title, String textMsg) {        this.title = title;        this.textMsg = textMsg;        this.creationDate = addCurrentDateTime();        noteContacts = new ArrayList<>();    }    void addContact(String name,                    String lastname,                    String phoneNumber,                    String email) {        Contact contact = new Contact(name, lastname, phoneNumber, email);        noteContacts.add(contact);    }    private static String addCurrentDateTime() {        Calendar cal = Calendar.getInstance();        SimpleDateFormat sdf = new SimpleDateFormat("EEE, HH:mm d MMM yyyy");        return sdf.format(cal.getTime());    }}