import java.text.SimpleDateFormat;import java.util.ArrayList;import java.util.Calendar;import java.util.List;public class Note {    private String title;    private String text;    private String creationDate;    private List<Contact> noteContacts;    Note(String title, String text) {        this.title = title;        this.text = text;        creationDate = addCurrentDateTime();        noteContacts = new ArrayList<>();    }    private static String addCurrentDateTime() {        Calendar cal = Calendar.getInstance();        SimpleDateFormat sdf = new SimpleDateFormat("EEE, HH:mm d MMM yyyy");        return sdf.format(cal.getTime());    }    public String getTitle() {        return title;    }    public String getText() {        return text;    }    public String getCreationDate() {        return creationDate;    }    public List<Contact> getNoteContacts() {        return noteContacts;    }    public void setTitle(String title) {        this.title = title;    }    public void setText(String text) {        this.text = text;    }    public void setCreationDate(String creationDate) {        this.creationDate = creationDate;    }    public void setNoteContacts(List<Contact> noteContacts) {        this.noteContacts = noteContacts;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Note note = (Note) o;        if (!getTitle().equals(note.getTitle())) return false;        if (!getText().equals(note.getText())) return false;        if (!getCreationDate().equals(note.getCreationDate())) return false;        return getNoteContacts().equals(note.getNoteContacts());    }    @Override    public int hashCode() {        int result = getTitle().hashCode();        result = 31 * result + getText().hashCode();        result = 31 * result + getCreationDate().hashCode();        result = 31 * result + getNoteContacts().hashCode();        return result;    }}