import java.text.SimpleDateFormat;import java.util.ArrayList;import java.util.Calendar;import java.util.List;public class Note {    private String title;    private String text;    private String creationDate;    private List<Contact> noteContacts;    Note(String title, String text) {        this.title = title;        this.text = text;        this.creationDate = addCurrentDateTime();        noteContacts = new ArrayList<>();    }    private static String addCurrentDateTime() {        Calendar cal = Calendar.getInstance();        SimpleDateFormat sdf = new SimpleDateFormat("EEE, HH:mm d MMM yyyy");        return sdf.format(cal.getTime());    }    public String getTitle() {        return title;    }    public String getText() {        return text;    }    public String getCreationDate() {        return creationDate;    }    public List<Contact> getNoteContacts() {        return noteContacts;    }}