import java.io.*;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class Notebook {    List<Note> notes = new ArrayList<>();    String notebookName;    public Notebook(String notebookName) {        this.notebookName = notebookName;    }    void addNote(Notebook notebook,                 Scanner scanner,                 String title,                 String textMsg) throws IOException {        Note note = new Note(title, textMsg);        notes.add(note);        System.out.println();        System.out.format("%nWould you like to add a contact? Type 1 if want or 0 if you don't%n");        int choice = scanner.nextInt();        if (choice == 1) {            System.out.print("name: ");            String name = scanner.next();            System.out.print("lastname: ");            String lastname = scanner.next();            System.out.print("phone: ");            String phoneNumber = scanner.next();            System.out.print("email: ");            String email = scanner.next();            note.addContact(name, lastname, phoneNumber, email);        }        System.out.println("note '" + title + "' is added");        showMainMenu(notebook, scanner);    }    void deleteNote(Notebook notebook,                    Scanner scanner,                    int index) throws IOException {        String title = notes.get(index).title;        notes.remove(index);        System.out.println("note '" + title + "' is deleted");        showMainMenu(notebook, scanner);    }    void printAllNotes(Notebook notebook,                       Scanner scanner) throws IOException {        int i = 1;        if (notes.size() == 0) {            System.out.println("No notes are added yet");            showMainMenu(notebook, scanner);        } else {            for (Note note : notes) {                System.out.println(i + " '" + note.title + "' " + note.creationDate);                i++;            }            System.out.format("%nType the note number to look through the note%n");            int n = scanner.nextInt();            while (n < 1 || n >= i) {                System.out.println("Can't find this note, try again");                n = scanner.nextInt();            }            printNote(notebook, scanner, n - 1);        }    }    void printNote(Notebook notebook,                   Scanner scanner,                   int index) throws IOException {        System.out.println("'" + notes.get(index).title + "'      Dated: " + notes.get(index).creationDate);        System.out.println("¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨");        System.out.println(notes.get(index).textMsg);        if (notes.get(index).noteContacts.size() != 0) {            System.out.format("¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨%nContacts:%n");            for (Contact contact : notes.get(index).noteContacts) {                System.out.println("name: " + contact.name);                System.out.println("lastname: " + contact.lastname);                System.out.println("phone: " + contact.phoneNumber);                System.out.println("email: " + contact.email);            }        }        System.out.println("¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨");        showMainMenu(notebook, scanner);    }}