import javax.print.DocFlavor;import java.util.ArrayList;import java.util.Scanner;public class Notebook {    ArrayList<Note> notes = new ArrayList<>();    int notesNumber = notes.size();    static Scanner reader = new Scanner(System.in);    String notebookName;    public Notebook(String notebookName) {        this.notebookName = notebookName;    }    public static void main(String[] args) {        Notebook myNotebook = new Notebook("My Notebook");        showMainMenu(myNotebook);    }    void addNote(Notebook notebook, String title, String textMsg) {        Note note = new Note(title, textMsg);        notes.add(note);        System.out.format("note #%d is added", notesNumber + 1);        showMainMenu(notebook);    }    void deleteNote(int index) {        String title = notes.get(index).title;        notes.remove(index);        System.out.println("note '" + title + "' is deleted");    }    void lookAllNotes(Notebook notebook) {        int i = 0;        if (notes.size() == 0) {            System.out.println("No notes are added yet");            showMainMenu(notebook);            return;        } else {            for (Note note : notes) {                System.out.println(i + " " + note.title + " " + note.creationDate);                i++;            }            System.out.println("Type the note number to look through the note");            int n = reader.nextInt();            printNote(notebook, n);            return;        }    }    void printNote(Notebook notebook, int index) {        System.out.println("'" + notes.get(index).title + "'");        System.out.println("------------------");        System.out.println(notes.get(index).creationDate);        System.out.println(notes.get(index).textMsg);        /*for (Contact contact : notes.get(index).noteContacts) {            System.out.println(contact.name);            System.out.println(contact.lastname);            System.out.println(contact.phoneNumber);            System.out.println(contact.email);        }*/        showMainMenu(notebook);        return;    }    static void showMainMenu(Notebook notebook) {        int i = 1;        System.out.println();        System.out.println("What would you like to do?");        System.out.println(i++ + " Show the list of notes");        System.out.println(i++ + " Add a note");        System.out.println(i++ + " Delete a note");        int n = reader.nextInt();        switch (n) {            case 1: {                notebook.lookAllNotes(notebook);                return;            }            case 2: {                String title;                String textMsg;                System.out.println("Give your note a title");                title = reader.nextLine();                System.out.println("Type your text");                textMsg = reader.nextLine();                notebook.addNote(notebook, title, textMsg);                return;            }            case 3: {                System.out.println("Choose the note number to delete");                notebook.lookAllNotes(notebook);                int j = reader.nextInt();                notebook.deleteNote(j);                System.out.println("The note is deleted");                return;            }        }    }}