import java.io.BufferedReader;import java.io.IOException;import java.util.List;public class NotebookService {    static String readLine;    static void addNote(List<Note> notes,                        String title,                        String text,                        BufferedReader reader) throws IOException {        Note note = new Note(title, text);        notes.add(note);        System.out.format("%nWould you like to add a contact? 1 YES | 0 NO%n");        int choice = enterInt(reader, readLine);        if (choice == 1) {            System.out.print("name: ");            String name = reader.readLine();            System.out.print("last name: ");            String lastName = reader.readLine();            System.out.print("phone: ");            String phoneNumber = reader.readLine();            System.out.print("email: ");            String email = reader.readLine();            NoteService.addContact(note, name, lastName, phoneNumber, email);            System.out.println();        }        System.out.format("%nnote '" + title + "' is added%n");        return;    }    static void deleteNote(List<Note> notes,                           int index) throws IOException {        String title = notes.get(index).title;        notes.remove(index);        System.out.format("%nnote '" + title + "' is deleted%n");        return;    }    static int printAllNotes(List<Note> notes, BufferedReader reader) throws IOException {        int i = 1;        if (notes.size() == 0) {            System.out.format("%nNo notes are added yet%n");            return 0;        } else {            for (Note note : notes) {                System.out.println(i++ + " '" + note.title + "' " + note.creationDate);            }        }        return 1;    }    static void chooseNote(List<Note> notes, BufferedReader reader) throws IOException {        System.out.format("%nType the note number to look through the note or 0 to back to the menu%n");        int n = enterInt(reader, readLine);        if (n == 0)            return;        while (n < 0 || n > notes.size()) {            System.out.format("%nCan't find this note, try again%n");            n = Integer.parseInt(reader.readLine());        }        printNote(notes, n - 1);    }    private static void printNote(List<Note> notes, int index) throws IOException {        System.out.println("'" + notes.get(index).title + "'      Dated: " + notes.get(index).creationDate);        System.out.println("¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨");        System.out.println(notes.get(index).text);        if (notes.get(index).noteContacts.size() != 0) {            System.out.format("¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨%nContacts:%n");            for (Contact contact : notes.get(index).noteContacts) {                System.out.println("name: " + contact.name);                System.out.println("last name: " + contact.lastname);                System.out.println("phone: " + contact.phoneNumber);                System.out.println("email: " + contact.email);            }        }        System.out.format("¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨¨%n");        return;    }    static int enterInt(BufferedReader reader, String readLine) throws IOException {        readLine = reader.readLine();        while (!isInteger(readLine)) {            System.out.println("Enter a valid number!");            readLine = reader.readLine();        }        return Integer.parseInt(readLine);    }    private static boolean isInteger(String s) {        try {            Integer.parseInt(s);        } catch (NumberFormatException e) {            return false;        } catch (NullPointerException e) {            return false;        }        return true;    }}