import java.util.ArrayList;import static java.lang.System.out;public class Task_2 {    public static void main(String[] args) {        ArrayList<Double> arrayList = new ArrayList<>();        Double a = 1.0;        double eps = 0.001;        int i = 0;        while (a > eps) {            a = Math.pow(Math.pow(i + 2, 2), -1);            arrayList.add(a);            i++;        }        for (Double element : arrayList) {            out.format("%.6f%n", element);        }        out.format("%n%d", i);    }}