import java.util.Random;public class Task_4 {    static int n = 10;    static int[] elements = new int[2 * n];    static int[] constructed_array = new int[n];        public static void main(String[] args) {        Random random = new Random();        for (int i = 0 ; i < 2 * n ; i++) {            elements[i] = random.nextInt(10);        }        for (int i = 0 ; i < n ; i++) {            constructed_array[i] = elements[i] + elements[2 * n - i - 1];        }        print_array(elements, "basic array: ");        print_array(constructed_array, "constructed array: ");        quickSort(constructed_array,0,n-1);        print_array(constructed_array,"sorted array: ");        System.out.format("%nmax element = %d",constructed_array[n-1]);    }    static void quickSort(int[] array, int left, int right) {        if (array == null || array.length == 0)            return;        if (left >= right)            return;                int middle = left + (right - left) / 2;        int pivot = array[middle];                int i = left, j = right;        while (i <= j) {            while (array[i] < pivot) {                i++;            }            while (array[j] > pivot) {                j--;            }            if (i <= j) {                int keeper = array[i];                array[i] = array[j];                array[j] = keeper;                i++;                j--;            }        }        if (left < j)            quickSort(array, left, j);        if (right > i)            quickSort(array, i, right);    }    static void print_array(int[] array, String comment){        System.out.print(comment);        for (int element: array) {            System.out.print(element+ " ");        }        System.out.println();    }}